import React, { useState, useEffect } from "react";
import './contact.css';
function Contact() {
  const [isSticky, setIsSticky] = useState(false);
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    phone: "",
    interests: "",
    ageGroup: "",
    country: "",
    reason: "",
    marketingConsent: false
  });

  // Handle scroll event for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 0);
      localStorage.setItem("scrollPosition", window.scrollY);
    };

    window.addEventListener("scroll", handleScroll);
    const savedScrollPosition = localStorage.getItem("scrollPosition");
    if (savedScrollPosition) {
      window.scrollTo(0, parseInt(savedScrollPosition, 10));
    }

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Handle form data changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const { fname, lname, email, phone, interests, ageGroup, country, reason, marketingConsent } = formData;

    if (!fname || !lname || !email || !phone || !interests || !ageGroup || !country || !reason) {
      alert("Please fill in all required fields.");
      return;
    }

    if (!validateEmail(email)) {
      alert("Please enter a valid email address.");
      return;
    }

    localStorage.setItem("user", JSON.stringify({ fname, lname, email }));
    sessionStorage.setItem("interest", JSON.stringify({ phone, interests }));

    alert("Form submitted successfully!");
  };

  // Email validation function
  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  // Handle menu toggle
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div>
      <section className="contact-home">
        <div className="headings">
          <h1>Contact Us</h1>
          <p>
            At SportsPathway, we value your feedback and inquiries. Please fill out the contact form below, and we will get back to you as soon as possible.
          </p>
        </div>
      </section>

      <section className="contact">
        <div className="container">
          <h1>Contact our team</h1>
          <form id="contactForm" onSubmit={handleSubmit}>
            <div className="form-group">
              <input type="text" name="fname" value={formData.fname} onChange={handleInputChange} placeholder="First name *" required />
              <input type="text" name="lname" value={formData.lname} onChange={handleInputChange} placeholder="Last name *" required />
            </div>
            <div className="form-group">
              <input type="email" name="email" value={formData.email} onChange={handleInputChange} placeholder="Email *" required />
              <input type="tel" name="phone" value={formData.phone} onChange={handleInputChange} placeholder="Phone number *" />
            </div>
            <div className="form-group">
              <input type="text" name="interests" value={formData.interests} onChange={handleInputChange} placeholder="Interests *" required />
            </div>
            <div className="form-group">
              <select name="ageGroup" value={formData.ageGroup} onChange={handleInputChange} required>
                <option value="">Age Group *</option>
                <option value="1-10">1-10</option>
                <option value="11-17">11-17</option>
                <option value="18-35">18-35</option>
                <option value="35+">35+</option>
              </select>
              <select name="country" value={formData.country} onChange={handleInputChange} required>
                <option value="">Country or region *</option>
                <option value="India">India</option>
                <option value="USA">USA</option>
                <option value="UK">UK</option>
                <option value="Australia">Australia</option>
              </select>
            </div>
            <div className="form-group">
              <select name="reason" value={formData.reason} onChange={handleInputChange} required>
                <option value="">Reason for contact *</option>
                <option value="pricing">Pricing</option>
                <option value="demo">Request a demo</option>
                <option value="support">Support</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div className="form-group">
              <textarea name="message" placeholder="Provide more details (optional)" onChange={handleInputChange}></textarea>
            </div>
            <div className="form-group checkbox-group">
              <input type="checkbox" name="marketingConsent" checked={formData.marketingConsent} onChange={handleInputChange} />
              <label htmlFor="marketingConsent">I agree to SportsPathway sending marketing communications about SportsPathway</label>
            </div>
            <button type="submit">Contact</button>
          </form>
        </div>
      </section>

      <button id="scrollToTopBtn" className="scroll-to-top" onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}>
        â†‘
      </button>
    </div>
  );
}

export default Contact;
