// src/components/Header.js
import React,{useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import '../pages/Header.css'; // Optional: Import a CSS file for styling

function Header() {
  const [scrolled, setScrolled] = useState(false);

  // Function to handle scroll event
  const handleScroll = () => {
    if (window.scrollY > 50) { // Change the value as needed
      setScrolled(true);
    } else {
      setScrolled(false);
    }
  };

  // Set up the scroll event listener
  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  return (
    <header>
      <a href="#" className="logo">
        <img src="/assets/logo.png" width="200px" height="110px" alt="Logo" />
      </a>

      <ul className="navigation">
        <li><Link to="/">HOME</Link></li>
        <li><Link to="/explore">EXPLORE</Link></li>
        <li><Link to="/latest">LATEST</Link></li>
        <li><Link to="/questionnaire">QUESTIONNAIRE</Link></li>
        <li><Link to="/login">LOGIN</Link></li>
      </ul>

      <div className="navtabs">
        <a href="#">Follow us</a>
        <a href="https://www.instagram.com/">
          <i className="ri-instagram-fill"></i>
        </a>
        <a href="https://x.com/">
          <i className="ri-twitter-x-fill"></i>
        </a>
        <a href="https://www.facebook.com/">
          <i className="ri-facebook-fill"></i>
        </a>
        <div className="bx bx-menu" id="menu"></div>
      </div>
    </header>
  );
}

export default Header;
