import React, { useEffect, useState } from "react";
import "../pages/Header.css";

const NewsSection = () => {
  const [news, setNews] = useState([]);
  const apiKey = "YOUR_API_KEY_HERE";
  const searchTerms = [
    "football", "basketball", "badminton", "tennis", "table tennis",
    "hockey", "skating", "wrestling", "archery", "hammer throw",
    "taekwondo", "golf", "shooting-sport", "swimming", "discus throw",
    "boxing", "javelin throw"
  ];
  
  const randomSearchTerm = searchTerms[Math.floor(Math.random() * searchTerms.length)];
  const apiUrl = `https://gnews.io/api/v4/search?q=${randomSearchTerm}&token=${apiKey}&lang=en&max=9`;

  useEffect(() => {
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => setNews(data.articles || []))
      .catch(error => console.error("Error fetching news:", error));
  }, [apiUrl]);

  return (
    <div className="news-container">
      {news.length === 0 ? (
        <p>No sports news found.</p>
      ) : (
        news.map((article, index) => (
          <div className="news-card" key={index}>
            <img src={article.image || "default-image.jpg"} alt="News" className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{article.title}</h5>
              <small className="text-muted">Published on: {new Date(article.publishedAt).toDateString()}</small>
              <p className="card-text">{article.description || "No description available"}</p>
              <a href={article.url} target="_blank" rel="noopener noreferrer" className="btn">Read more</a>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default NewsSection;
