// src/components/Header.js
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../pages/Header.css';

class Header extends Component {
  // Define the component's state
  constructor(props) {
    super(props);
    // State to manage whether the burger menu is open or not
    this.state = {
      isMenuOpen: false
    };
  }

  // Method to toggle the burger menu open/close
  toggleMenu = () => {
    this.setState(prevState => ({
      isMenuOpen: !prevState.isMenuOpen
    }));
  };

  // Component Lifecycle: componentDidMount (useful for future additions like fetching data)
  componentDidMount() {
    // Example lifecycle method - could be used for analytics or to check if a user is logged in
    console.log("Header component mounted");
  }

  // Event Handler for the burger icon click
  handleBurgerClick = () => {
    this.toggleMenu();
  };

  // Render the header with dynamic menu state (open or closed)
  render() {
    const { isMenuOpen } = this.state;

    return (
      <header>
        {/* Logo Section */}
        <a href="#" className="logo">
          <img src="/assets/logo.png" width="200px" height="110px" alt="Logo" />
        </a>

        {/* Navigation Menu */}
        <ul className={`navigation ${isMenuOpen ? 'open' : ''}`}>
          <li><Link to="/">HOME</Link></li>
          <li><Link to="/explore">EXPLORE</Link></li>
          <li><Link to="/latest">LATEST</Link></li>
          <li><Link to="/questionnaire">QUESTIONNAIRE</Link></li>
          <li><Link to="/contact">CONTACT</Link></li>
          <li><Link to="/login">LOGIN</Link></li>
        </ul>

        {/* Social Media and Burger Menu */}
        <div className="navtabs">
          <a href="#">Follow us</a>
          <a href="https://www.instagram.com/">
            <i className="ri-instagram-fill"></i>
          </a>
          <a href="https://x.com/">
            <i className="ri-twitter-x-fill"></i>
          </a>
          <a href="https://www.facebook.com/">
            <i className="ri-facebook-fill"></i>
          </a>
          
          {/* Burger Menu Icon */}
          <div className={`bx bx-menu ${isMenuOpen ? 'active' : ''}`} id="menu" onClick={this.handleBurgerClick}></div>
        </div>
      </header>
    );
  }
}

export default Header;
