import React from 'react';
import './about.css'; 
function About() {
  return (
    <div>
      <section className="about1">
      </section>
      <section className="about">
        <div className="content">
          Welcome to SportsPathway, the ultimate online platform dedicated to promoting sports development, athlete empowerment, and community engagement. At SportsPathway, we believe that sports are more than just games—they are a powerful force for personal growth, health, and social connection. Our mission is to inspire and guide the next generation of athletes, coaches, and sports enthusiasts, helping them discover their true potential and passion for sports.

          <br />What We Do:
          <br /> Personalized Sport Recommendations: Unsure which sport is right for you? Our interactive questionnaire helps you find the perfect match based on your interests, skills, and goals. Whether you're a beginner or a seasoned athlete, we'll guide you to the sport that fits you best.
          <br />In-Depth Sports Information: Explore a wide range of sports, from the popular to the niche. Learn about the rules, techniques, training methods, and opportunities available in each sport. Stay updated with the latest trends and insights from the world of sports.
          <br />Community Connection: Connect with fellow sports enthusiasts, athletes, coaches, and organizations. Share experiences, ask questions, and learn from a community that shares your passion.
          <br />Pathway to Opportunities: Whether you're looking to start a new sport, advance your career, or simply stay active and healthy, SportsPathway is here to help. Discover pathways to scholarships, coaching, competitions, and other opportunities that align with your aspirations.
          <br />
          Join us in fostering a culture of sportsmanship, collaboration, and growth. At SportsPathway, we're more than just a platform—we're a community dedicated to making sports accessible and enjoyable for everyone.
        </div>
      </section>
    </div>
  );
}

export default About;
