import React from 'react';
import './explore.css'; // Ensure the CSS file is imported

const Explore = () => {
  return (
    <div>
      <section className="explore-home">
        <div className="headings">
          <h1>Explore</h1>
          <p>
            Welcome to SportsPathway's Sports Exploration page! Here we invite you
            to enter the world of sports passion and explore our diverse games.
            From the skill of soccer and tennis to the power of basketball and
            volleyball, each sport has its own unique sense of excitement and
            competition. Our interactive map highlights the importance of each
            game, allowing you to learn more about the history, technology, and
            communities that have grown up around it. Whether you're a seasoned
            athlete or a new enthusiast, we want you to discover, get involved,
            and find your way in the sport you love. Join us in this message to
            support the sport!
          </p>
        </div>
      </section>
      <section className="explore1">
        <div className="explore-container">
          {renderCard("football", "FOOTBALL", "The beautiful game. Known for its tactical complexity and passionate fans.", "assets/football.jpg", "football.html")}
          {renderCard("badmin", "BADMINTON", "A fast-paced and technical racket sport requiring agility and precision.", "images/badmin.jpg", "badminton.html")}
          {renderCard("ttennis", "TABLE TENNIS", "A thrilling game of reflexes and strategy played on a miniature court.", "images/ttennis.jpg", "tabletennis.html")}
        </div>
        <div className="explore-container1">
          {renderCard("tennis", "TENNIS", "The elegant duel. Known for its strategic finesse, intense rallies, and individual brilliance.", "images/tennis-girl-professional-tennis-court_654080-1256.avif", "tennis.html")}
          {renderCard("squash", "SQUASH", "The fast-paced challenge. Known for its agility, quick reflexes, and relentless endurance within the confines of a glass-walled court.", "images/images.jpeg", "squash.html")}
          {renderCard("archery", "ARCHERY", "The art of precision. Known for its focus, steady hand, and the pursuit of the perfect shot in complete silence.", "images/archer.jpg", "archery.html")}
        </div>
        <div className="explore-container2">
          {renderCard("golf", "GOLF", "The serene pursuit. Known for its blend of mental fortitude, precision, and the challenge of mastering the landscape with every swing.", "images/gol.jpg", "golf.html")}
          {renderCard("javelin", "JAVELIN THROW", "The power of flight. Known for its explosive strength, perfect technique, and the quest to send the spear soaring as far as possible.", "images/javelin.jpg", "javelin2.html")}
          {renderCard("boxing", "BOXING", "The sweet science. Known for its blend of strategy, raw power, agility, and the relentless spirit of the fight.", "images/box.jpg", "boxing2.html")}
        </div>
        <div className="explore-container3">
          {renderCard("swimming", "SWIMMING", "The aquatic race. Known for its rhythmic strokes, endurance, and the drive to conquer the water with speed and grace.", "images/swim.jpg", "swimming2.html")}
          {renderCard("discus", "DISCUS THROW", "The spin of strength. Known for its balance, explosive power, and the art of hurling the disc with precision and maximum distance.", "images/discus.jpg", "discus02.html")}
          {renderCard("volleyball", "VOLLEYBALL", "The dynamic battle. Known for its teamwork, quick reflexes, and powerful spikes in a high-energy, fast-paced court game.", "images/volley.jpg", "volleyball.html")}
        </div>
        <div className="explore-container4">
          {renderCard("basketball", "BASKETBALL", "The court showdown. Known for its fast breaks, precise shooting, and the artistry of teamwork and skillful plays.", "images/basket.jpg", "basketball.html")}
          {renderCard("wrestling", "WRESTLING", "The ultimate clash. Known for its raw strength, strategic grappling, and the intense physical and mental battle for dominance on the mat.", "images/wrestling.jpg", "wrestling.html")}
          {renderCard("skating", "SKATING", "The graceful glide. Known for its blend of balance, fluid movement, and the artistry of executing intricate routines or navigating challenging courses.", "images/skating.jpg", "skating.html")}
        </div>
        <div className="explore-container5">
          {renderCard("hammer", "HAMMER THROW", "The forceful launch. Known for its powerful spins, explosive strength, and the precise technique required to hurl the heavy hammer as far as possible.", "images/hammer.jpg", "Hammer throw.html")}
          {renderCard("shooting", "SHOOTING", "The test of focus. Known for its precision, steady hand, and the skill to achieve perfect accuracy under pressure.", "images/shooting.jpg", "shooting.html")}
          {renderCard("hockey", "HOCKEY", "The fast-paced clash. Known for its intense speed, skillful stickhandling, and the strategic battle for control on ice or turf.", "images/hockey.jpg", "hockey.html")}
        </div>
      </section>
    </div>
  );
};

const renderCard = (id, title, description, imgSrc, link) => (
  <div className="explore-card" >
    <img src={imgSrc} alt={title} />
    <div className="explore-card-content">
      <h3>{title}</h3>
      <p>{description}</p>
      <center><a href={link}>Learn more</a></center>
    </div>
  </div>
);

export default Explore;
