import React, { useEffect, useState } from 'react';
import './latest.css'; // Ensure the CSS file is imported

const NewsSection = () => {
  const [newsArticles, setNewsArticles] = useState([]);
  const apiKey = "02b5eb252808641b434b28b2f63b9fbf";
  const searchTerms = [
    "football", "basketball", "badminton", "tennis", "table tennis", "hockey",
    "skating", "wrestling", "archery", "hammer throw", "taekwondo", "golf",
    "shooting-sport", "swimming", "discus throw", "boxing", "javelin throw"
  ];

  const fetchNews = async () => {
    const randomSearchTerm = searchTerms[Math.floor(Math.random() * searchTerms.length)];
    const apiUrl = `https://gnews.io/api/v4/search?q=${randomSearchTerm}&token=${apiKey}&lang=en&max=9`;

    try {
      const response = await fetch(apiUrl);
      if (!response.ok) throw new Error("Failed to fetch the news.");
      const data = await response.json();
      setNewsArticles(data.articles || []);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  useEffect(() => {
    fetchNews();
  }, []);

  return (
    <div>
      <header>
        <a href="#" className="logo">
          <img src="images/logo.png" width="200px" height="110px" alt="Logo" />
        </a>
        <ul className="navigation">
          <li><a href="index.html">HOME</a></li>
          <li><a href="explore.html">EXPLORE</a></li>
          <li><a href="latest.html">LATEST</a></li>
          <li><a href="contact.html">CONTACT US</a></li>
          <li><a href="questionnaire.html">QUESTIONNAIRE</a></li>
          <li><a href="login.html">LOGIN</a></li>
        </ul>
        <div className="navtabs">
          <a href="">Follow us</a>
          <a href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer"><i className="ri-instagram-fill"></i></a>
          <a href="https://x.com/" target="_blank" rel="noopener noreferrer"><i className="ri-twitter-x-fill"></i></a>
          <a href="https://www.facebook.com/" target="_blank" rel="noopener noreferrer"><i className="ri-facebook-fill"></i></a>
          <div className="bx bx-menu" id="menu"></div>
        </div>
      </header>
      <section className="home">
        <div className="headings">
          <h5>Latest</h5>
          <h1>Sports News</h1>
          <p>
            Stay up to date with the latest sports news across a variety of
            disciplines. From football to tennis, basketball to athletics, we've
            got you covered with the freshest updates, match results, and player
            highlights. Get real-time insights into the world of sports and never
            miss a moment of action.
          </p>
        </div>
      </section>
      <div className="container">
        <div id="news-details" className="news-container">
          {newsArticles.length > 0 ? (
            newsArticles.map((article, index) => (
              <div key={index} className="news-card">
                <div className="card">
                  <img src={article.image || "default-image.jpg"} alt={article.title} className="card-img-top" />
                  <div className="card-body">
                    <h5 className="card-title">{article.title}</h5>
                    <small className="text-muted">Published on: {new Date(article.publishedAt).toDateString()}</small>
                    <p className="card-text">{article.description || "No description available"}</p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer" className="btn">Read more</a>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>No sports news found.</p>
          )}
        </div>
      </div>
      <section className="footer">
        <div className="footer-box">
          <h3>Services</h3>
          <a href="questionnaire.html">Questionnaire</a>
          <a href="latest.html">Latest</a>
        </div>
        <div className="footer-box">
          <h3>About</h3>
          <a href="about.html">About Us</a>
        </div>
        <div className="footer-box">
          <h3>Help</h3>
          <a href="FAQ.html">FAQs</a>
          <a href="contact.html">Contact us</a>
        </div>
        <div className="footer-box">
          <h3>Social</h3>
          <div className="social">
            <a href="https://www.instagram.com/"><i className="ri-instagram-fill"></i></a>
            <a href="https://x.com/"><i className="ri-twitter-x-fill"></i></a>
            <a href="https://www.facebook.com/"><i className="ri-facebook-fill"></i></a>
          </div>
        </div>
      </section>
    </div>
  );
};

export default NewsSection;
